mapper.select:
select id, name, status
from (
	select rownum rnum, id, name, status
	from (
		select id, name, status
		from tbl_member
		where 1 = 1

		order by id 
		)
	)
	where rnum between #{startRow} and #{endRow}

select id, name, status, regDate
from tbl_member
where id = #{id}

mapper.update:
update tbl_member
set title = #{title}, content = #{content}, writer = #{writer}
where no = #{no}

mapper.select:
select id,name
from tbl_member
where id = #{id} and pw = #{pw}
